package info.magnolia.cms.cache;

import info.magnolia.cms.security.Digester;
import info.magnolia.context.MgnlContext;

import java.util.Collection;

import javax.servlet.http.HttpServletRequest;

import org.apache.commons.lang.ArrayUtils;


/**
 * @author Sameer Charles $Id$
 */
public class RoleBasedCacheFilter extends CacheFilter {

    /**
     * This will: - Consolidate all roles sorted by node name - Generate MD5 of this consolidated string - Adds this MD5
     * to the key generated by cache manager
     */
    public String getCacheKey(HttpServletRequest request) {
        String key = MgnlContext.getAggregationState().getCurrentURI();
        return this.generateMD5Key(MgnlContext.getUser().getAllRoles(), key);
    }

    /**
     * generate an MD5 of all roles assigned to this user
     * @param assignedRoles
     * @param key as created by configured CacheManager
     */
    private String generateMD5Key(Collection assignedRoles, String key) {
        String asString = ArrayUtils.toString(assignedRoles);
        StringBuffer buffer = new StringBuffer(key).append(".").append(Digester.getMD5Hex(asString));
        return buffer.toString();
    }

}
